import React from 'react';
import styled from 'styled-components';
import Link from 'next/link'
import {useState, useEffect } from 'react';
import {Menu} from "antd";



const Nav=()=>{
    const [show, setShow]=useState(true);


    const Navigation = styled(Menu)`
      position: fixed;
      top:0;
      width:100%;
      z-index: 1;
      border: none;
      background-color: ${show ? '#668cff':`rgba(102,140,255,${1-window.scrollY/300})`};
      font-size:2.5rem;
      height:8vh;
      display: flex;
      
    `;


    useEffect(()=>{
        window.addEventListener("scroll",()=>{
            if(window.scrollY<100){
                setShow(true);
            }else{
                setShow(false);
            }
        })
        return ()=>{
            window.removeEventListener("scroll",null);
        }
    },[]);

    return(
        <>
            {
                show ?
                    <Navigation mode="horizontal">
                        <Navigation.Item>
                            <Link href="/"><a>Reable</a></Link>
                        </Navigation.Item>
                    </Navigation> :
                    <Navigation mode="horizontal">
                        <Navigation.Item>
                            <Link href="/"><a>Earth</a></Link>
                        </Navigation.Item>
                    </Navigation>
            }



        </>
    )
}
export default Nav;
