import React from 'react';
import {Carousel, Row,Col,Input,Button,Image} from 'antd';
import styled from 'styled-components';

const ContentStyle = styled.div`
  background-image: url(${props=>props.background});
  background-size: contain;
  height: 80vh;
  color: #fff;
`;

const StyledH1 =styled.h1`
  font-size: 2.5rem;
`
const StyledH3 =styled.h3`
  font-size: 1.2rem;
`
const StyledH5 =styled.h5`
  font-size: 0.7rem;
`

const Main=()=>{
    return (
        <div>
            <Carousel autoplay>
                <ContentStyle background="../img/main1.jpg">
                    <Row style={{marginLeft:'3vh'}}>
                        <Col style={{whiteSpace:'nowrap', marginTop:'10vh'}}>
                            <Col xs={24} md={6}>
                                <StyledH1> Reable </StyledH1>
                                <StyledH3>
                                    환경을 살리는 다회용컵 서비스<br/>
                                    이메일을 적어주시면 리에이블 서비스에<br/>
                                    대한 알림을 보내드립니다.
                                </StyledH3>
                            </Col>
                            <Col xs={24} md={6} >
                                <Col style={{whiteSpace:'nowrap'}}>
                                    <Input placeholder="Email" style={{marginRight:'1vh',width: '30vh'}} />
                                    <Button type="primary">등록</Button>
                                </Col>
                            </Col>
                            <Col xs={24} md={2}>
                                <StyledH5>
                                    이메일은 리에이블 서비스 알림을<br/>
                                    전송하는데에만 사용되며 제3자에게 제공되지않습니다
                                </StyledH5>
                            </Col>
                            <Col xs={24} md={10}>
                                <Image with={60} src={""}/>
                            </Col>
                        </Col>
                    </Row>
                </ContentStyle>

            </Carousel>
        </div>
    );
}

export default Main;
